import classes from "./postMain.module.css";
import { Cloudinary } from "@cloudinary/url-gen";

function PostMain(props) {

    const cld = new Cloudinary({cloud: {cloudName: 'pear-programming'}}); 
    const postImage = cld.image(props.postImage);


    return (
        <div className={classes.postMain}>
            {props.postImage ? <>
                <div className={classes.postImage}><img src={postImage.toURL()}/></div>
                
            </>: null}
            <h1 className={classes.postTitle}>{props.postTitle}</h1>
            <p className={classes.postDesc}>{props.postDesc}</p>
        </div>
    );
}

export default PostMain;
