import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import NavBar from "../components/layout/navBar";
import PostList from "../components/posts/postList";
import PageContainer from "../components/layout/page-container";
import MainContent from "../components/layout/main-content";
import NavSidebar from "../components/layout/nav-sidebar/navSidebar";
import { Link } from "react-router-dom";
import heartIcon from "../assets/icons/plusIcon.svg";
import classes from "./Feed.module.css";
import NavSidebarLink from "../components/layout/nav-sidebar/navSidebarLink";

function Feed() {
  const [tag, setTag] = useState(null);

  const navigate = useNavigate();

  function updateTag(e){
    if(e.keyCode == 13){
      console.log(tag);
      navigate("?term="+tag)
      setTag(null);
    }
  }

  return (
    <div>
      <NavBar />
      
      <PageContainer>
        <NavSidebar>
          <div className={classes.searchTags}><input onKeyDown={updateTag} value={tag || ""} onChange={(e)=>setTag(e.target.value)} type="text" placeholder="Search Tags"/></div>
          <NavSidebarLink to="?sortBy=mostlikes" linkTitle="Most Likes"/>
          <NavSidebarLink to="?sortBy=newest" linkTitle="Newest"/>
          <NavSidebarLink to="?likes=true" linkTitle="Your Likes"/>
          <Link className={classes.newPostButton} to="/submit">Create Post <img src={heartIcon}></img></Link>
        </NavSidebar>
        <MainContent>
          <PostList />
        </MainContent>
      </PageContainer>
    </div>
  );
}

export default Feed;
