import React, { useEffect, useState } from "react";
import classes from "./postHeader.module.css";
import threeDotsSVG from "../../../assets/icons/three-dots.svg";

function PostHeader(props) {
    const [userInfo, setUserInfo] = useState([])

    const options = { year: "numeric", month: "long", day: "numeric", hour: '2-digit', minute: '2-digit'}
    const date = new Date(props.timestamp).toLocaleDateString(undefined, options);

    useEffect(() => {
        setUserInfo(props.userInfo);
    });

    return (
        <div className={classes.postHeader}>
            <div className={classes.postHeaderMain}>
                <div className={classes.PFP}>
                    { userInfo.firstName ? <p className={classes.profileChar}>{userInfo.firstName.charAt(0).toUpperCase()}</p> : null }
                    { userInfo.profilePicture ? <img src={userInfo.profilePicture}/> : null }
                </div>
                <div className={classes.username}>
                    { userInfo.firstName && userInfo.lastName ? <p className={classes.displayName}>{userInfo.firstName} {userInfo.lastName}</p> : null}
                    <p className={classes.timestamp}>{date}</p>
                </div>
            </div>
            <div className={classes.postHeaderOptionsBtn}>
                <img src={threeDotsSVG}/>
            </div>
        </div>
    );
}

export default PostHeader;
