import React, { useState, useEffect } from "react";
import classes from "./post.module.css";
import Axios from "axios";
import PostHeader from "./postHeader";
import PostMain from "./postMain";
import CategoryBar from "./categoryBar";
import PostTech from "./postTech"
import PostInteractions from "./postInteractions";

function Post(props) {
  const [userInfo, setUserInfo] = useState([]);

  // Get user info from userID
  useEffect(() => {
    Axios.post("http://localhost:3001/users/user", {userID: props.postData.userID}).then((response) => {
      setUserInfo(response.data);
    });
  }, [props.postData.userID]);

  return (
    <div className={classes.post}>
      <PostHeader userInfo={userInfo} timestamp={props.postData.timestamp}/>
      <CategoryBar postCategory={props.postData.category}/>
      <PostMain postTitle={props.postData.title} postDesc={props.postData.description} />
      <PostTech postTech={props.postData.technologies}/>
      <PostInteractions postLikes={props.postData.likes} postID={props.postData._id} />
    </div>
  );
}

export default Post;
